import React from "react";

class SearchBar extends React.Component {
    constructor(props) {
        super(props)
        this.state = { term: "" }
    }

    onChangeHandle = (e) => {
        this.setState({ term: e.target.value })
        console.log(this.state.term)
    }

    onSubmit(e) {
        e.preventDefault
    }

    render() {
        return <div>
            <form onSubmit={this.onSubmit}>
                <label>Search: </label>
                <input value={this.state.value} onChange={this.onChangeHandle} placeholder="enter your word here"></input>
            </form>
        </div >
    }
}

export default SearchBar;
